(3667, 218, '_work_section', 'field_629dc5e2c8d5c'),
(3668, 218, 'tab_section', '<p style=\"text-align: center;\">Sustainability is an ongoing journey.\r\nOur program is designed without an end date.\r\nUntil you are at the end of your journey and you have fulfilled all of your goals, we will be here to support you!</p>'),
(3669, 218, '_tab_section', 'field_629ddff728c1f'),
(3670, 218, 'text_section', '<h3 style=\"text-align: center;\">Sustainability is an ongoing journey.\r\nOur program is designed without an end date.\r\nUntil you are at the end of your journey and you have fulfilled all of your goals, we will be here to support you!</h3>'),
(3671, 218, '_text_section', 'field_629ddff728c1f'),
(3672, 218, 'futures_section_title', 'Who is Ki Futures for?'),
(3673, 218, '_futures_section_title', 'field_629decc94ddd2'),
(3674, 218, 'futures_section_text', 'Ki Futures is for everyone. Every cultural actor, professional, individual, organization, or company who believe that culture has a role to play in a sustinable future and wants to take action.\r\n\r\n&nbsp;\r\n\r\nOrganizations, associations, institutions, museums, artists, galleries, conservators, freelance professionals and private practitioners, vendors, suppliers, packing and shipping companies, and anyone else who is considered to be a cultural professional is welcome.\r\n\r\n&nbsp;\r\n\r\nSustainability requires a global effort, and everyone is welcome to be a part of the solution!'),
(3675, 218, '_futures_section_text', 'field_629dece44ddd3'),
