INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(4766, 226, 'work_section_text', 'Sustainability affects us all - and we all have a role to play. Ki Futures is an international and holistic program which brings together the entire cultural sector to face and solve our challenges through co-creation, collaboration, and knowledge exchange.\r\n\r\nThrough coaching, training, workshops, global networking meetings, guest speakers, and more, participants have the opportunity to gain the knowledge and skills needed to implement sustainability in their practices. Participants are encouraged to work together to create international, cross-cultural projects and relationships.'),
(4767, 226, '_work_section_text', 'field_629dc615c8d5e'),
(4768, 226, 'work_section_image', '59'),
(4769, 226, '_work_section_image', 'field_629dc62ac8d5f'),
