(5076, 228, 'works_section_works_data_list_1_text', '<p style=\"text-align: center;\">The program was co-developed with international sustainability and culture experts and draws from real solutions and with inspiration and collaboration from other sectors.</p>'),
(5077, 228, '_works_section_works_data_list_1_text', 'field_629ee27346b7a'),
