(6008, 238, 'hero_section_text', 'Ki Futures is an international coaching and training network for sustainability in the cultural sector.'),
(6009, 238, '_hero_section_text', 'field_6299ec3892f79'),
(6010, 238, 'program_section_title', 'Ki Futures Program'),
(6011, 238, '_program_section_title', 'field_629da513dc0d0'),
