(6054, 238, 'works_section_works_data_list_1_text', '<p style=\"text-align: center;\">The program was co-developed with international sustainability and culture experts and draws from real solutions and with inspiration and collaboration from other sectors.</p>'),
(6055, 238, '_works_section_works_data_list_1_text', 'field_629ee27346b7a'),
(6056, 238, 'works_section_works_data_list_2_image', '91'),
(6057, 238, '_works_section_works_data_list_2_image', 'field_629ee24046b79'),
(6058, 238, 'works_section_works_data_list_2_text', '<p style=\"text-align: center;\">The Ki Port streamlines sharing best practices and promotes peer-to-peer learning. Ki Futures cultivates cultures of sustainability within institutions and provides continued support on every level.</p>'),
(6059, 238, '_works_section_works_data_list_2_text', 'field_629ee27346b7a'),
