INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(6465, 249, '_futures_section_title', 'field_629decc94ddd2'),
(6466, 249, 'futures_section_text', 'Ki Futures is for everyone. Every cultural actor, professional, individual, organization, or company who sees cultures\' role to play in a sustainable future and wants to take action.\r\n\r\n&nbsp;\r\n\r\nOrganizations, associations, institutions, museums, theaters, opera houses, artists, galleries, conservators, freelance professionals and private practitioners, educators, professors, students, researchers, vendors, suppliers, packing and shipping companies, and anyone else who considers themselves a cultural professional is welcome.\r\n\r\n&nbsp;\r\n\r\nSustainability requires a global effort, and everyone is part of the solution!'),
