(8141, 294, '_works_section_works_data_list_0_image', 'field_629ee24046b79'),
(8142, 294, 'works_section_works_data_list_0_text', '<p style=\"text-align: center;\">Ki Futures was created by a conservator, who is also the Secretary of the ICOM Working Group on Sustainability and the founder of Ki Culture.</p>'),
(8143, 294, '_works_section_works_data_list_0_text', 'field_629ee27346b7a'),
(8144, 294, 'works_section_works_data_list_1_image', '92'),
(8145, 294, '_works_section_works_data_list_1_image', 'field_629ee24046b79'),
(8146, 294, 'works_section_works_data_list_1_text', '<p style=\"text-align: center;\">The program was co-developed with international sustainability and culture experts and draws from real solutions and with inspiration and collaboration from other sectors.</p>'),
(8147, 294, '_works_section_works_data_list_1_text', 'field_629ee27346b7a'),
(8148, 294, 'works_section_works_data_list_2_image', '91'),
(8149, 294, '_works_section_works_data_list_2_image', 'field_629ee24046b79'),
(8150, 294, 'works_section_works_data_list_2_text', '<p style=\"text-align: center;\">The Ki Port streamlines sharing best practices and promotes peer-to-peer learning. Ki Futures cultivates cultures of sustainability within institutions and provides continued support on every level.</p>'),
(8151, 294, '_works_section_works_data_list_2_text', 'field_629ee27346b7a'),
(8152, 294, 'works_section_works_data_list_3_image', '94'),
(8153, 294, '_works_section_works_data_list_3_image', 'field_629ee24046b79'),
(8154, 294, 'works_section_works_data_list_3_text', '<p style=\"text-align: center;\">The program turns talk into action - providing tangible benchmarks and outputs. It supports participants in creating sustainability initiatives (like green teams) within their organizations and local networks.</p>'),
