(8621, 300, '_works_section_works_data_list_0_image', 'field_629ee24046b79'),
(8622, 300, 'works_section_works_data_list_0_text', '<p style=\"text-align: center;\">Ki Futures was created by a conservator, who is also the Secretary of the ICOM Working Group on Sustainability and the founder of Ki Culture.</p>'),
(8623, 300, '_works_section_works_data_list_0_text', 'field_629ee27346b7a'),
