(8626, 300, 'works_section_works_data_list_1_text', '<p style=\"text-align: center;\">The program was co-developed with international sustainability and culture experts and draws from real solutions and with inspiration and collaboration from other sectors.</p>'),
(8627, 300, '_works_section_works_data_list_1_text', 'field_629ee27346b7a'),
(8628, 300, 'works_section_works_data_list_2_image', '91'),
(8629, 300, '_works_section_works_data_list_2_image', 'field_629ee24046b79'),
(8630, 300, 'works_section_works_data_list_2_text', '<p style=\"text-align: center;\">The Ki Port streamlines sharing best practices and promotes peer-to-peer learning. Ki Futures cultivates cultures of sustainability within institutions and provides continued support on every level.</p>'),
(8631, 300, '_works_section_works_data_list_2_text', 'field_629ee27346b7a'),
(8632, 300, 'works_section_works_data_list_3_image', '94'),
(8633, 300, '_works_section_works_data_list_3_image', 'field_629ee24046b79'),
(8634, 300, 'works_section_works_data_list_3_text', '<p style=\"text-align: center;\">The program turns talk into action - providing tangible benchmarks and outputs. It supports participants in creating sustainability initiatives (like green teams) within their organizations and local networks.</p>'),
