INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(12365, 527, 'program_section_text', 'Ki Futures empowers organizations, individuals, companies - all cultural stakeholders to embed sustainability in their practice and inspire their communities.\r\n\r\n&nbsp;\r\n\r\nKi Futures provides training, workshops, coaches, and an international network of cultural professionals, sustainability specialists, and partner organizations. We provide the critical thinking skills and understanding of sustainability theoretically and work collaboratively to find new paths and solutions for sustainability for culture - on individual, institutional, and sectoral levels. The program provides tangible outcomes through various collective projects as well as support systems to reach individual goals.'),
(12366, 527, '_program_section_text', 'field_629da7986ad76'),
(12367, 527, 'program_section_image', '49'),
(12368, 527, '_program_section_image', 'field_629da7fb6ad77'),
(12369, 527, 'work_section_title', 'How does it work?'),
(12370, 527, '_work_section_title', 'field_629dc5fcc8d5d'),
(12371, 527, 'work_section_text', 'Sustainability affects us all - and we all have a role to play. Ki Futures is an international and holistic program which brings together the entire cultural sector to face and solve our challenges through co-creation, collaboration, and knowledge exchange.\r\n\r\n&nbsp;\r\n\r\nThrough coaching, training, workshops, global networking meetings, guest speakers, and more, participants have the opportunity to gain the knowledge and skills needed to implement sustainability in their practices. Participants are encouraged to work together to create international, cross-cultural projects and relationships.'),
(12372, 527, '_work_section_text', 'field_629dc615c8d5e'),
(12373, 527, 'work_section_image', '521'),
(12374, 527, '_work_section_image', 'field_629dc62ac8d5f'),
