(15057, 598, '_works_section_works_data_list_0_image', 'field_629ee24046b79'),
(15058, 598, 'works_section_works_data_list_0_text', '<h6>Ki Futures was created by a conservator, who is also the Secretary of the ICOM Working Group on Sustainability and the founder of Ki Culture.</h6>'),
(15059, 598, '_works_section_works_data_list_0_text', 'field_629ee27346b7a'),
