(19768, 5, 'hero_section_text', 'Ki Futures is an international coaching and training network for sustainability in the cultural sector.'),
(19769, 5, '_hero_section_text', 'field_6299ec3892f79'),
(19770, 5, 'program_section_title', 'Ki Futures Program'),
(19771, 5, '_program_section_title', 'field_629da513dc0d0'),
