(21706, 5, 'hero_section_text', 'Ki Futures is an international coaching and training network for sustainability in the cultural sector.'),
(21707, 5, '_hero_section_text', 'field_6299ec3892f79'),
(21708, 5, 'program_section_title', 'Ki Futures Program'),
(21709, 5, '_program_section_title', 'field_629da513dc0d0'),
