(23900, 978, 'hero_section_text', 'Ki Futures is an international coaching and training network for sustainability in the cultural sector.'),
(23901, 978, '_hero_section_text', 'field_6299ec3892f79'),
(23902, 978, 'program_section_title', 'Ki Futures Program'),
(23903, 978, '_program_section_title', 'field_629da513dc0d0'),
