INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(24204, 979, 'program_section_text', 'Ki Futures empowers organizations, individuals, companies - all cultural stakeholders to embed sustainability in their practice and inspire their communities.\r\n\r\n&nbsp;\r\n\r\nKi Futures provides training, workshops, coaches, and an international network of cultural professionals, sustainability specialists, and partner organizations. We provide the critical thinking skills and understanding of sustainability theoretically and work collaboratively to find new paths and solutions for sustainability for culture - on individual, institutional, and sectoral levels. The program provides tangible outcomes through various collective projects as well as support systems to reach individual goals.'),
(24205, 979, '_program_section_text', 'field_629da7986ad76'),
(24206, 979, 'program_section_image', '49'),
(24207, 979, '_program_section_image', 'field_629da7fb6ad77'),
(24208, 979, 'work_section_title', 'How does it work?'),
(24209, 979, '_work_section_title', 'field_629dc5fcc8d5d'),
(24210, 979, 'work_section_text', 'Sustainability affects us all - and we all have a role to play. Ki Futures is an international and holistic program which brings together the entire cultural sector to face and solve our challenges through co-creation, collaboration, and knowledge exchange.\r\n\r\n&nbsp;\r\n\r\nThrough coaching, training, workshops, global networking meetings, guest speakers, and more, participants have the opportunity to gain the knowledge and skills needed to implement sustainability in their practices. Participants are encouraged to work together to create international, cross-cultural projects and relationships.'),
(24211, 979, '_work_section_text', 'field_629dc615c8d5e'),
(24212, 979, 'work_section_image', '521'),
(24213, 979, '_work_section_image', 'field_629dc62ac8d5f'),
(24214, 979, 'work_section', ''),
(24215, 979, '_work_section', 'field_629dc5e2c8d5c'),
(24216, 979, 'tab_section', '<p style=\"text-align: center;\">Sustainability is an ongoing journey.\r\nOur program is designed without an end date.\r\nUntil you are at the end of your journey and you have fulfilled all of your goals, we will be here to support you!</p>'),
(24217, 979, '_tab_section', 'field_629ddff728c1f'),
(24218, 979, 'text_section', ''),
(24219, 979, '_text_section', 'field_629ddff728c1f'),
(24220, 979, 'futures_section_title', 'Who is Ki Futures for?'),
(24221, 979, '_futures_section_title', 'field_629decc94ddd2'),
(24222, 979, 'futures_section_text', 'Ki Futures is for everyone. Every cultural actor, professional, individual, organization, or company who sees cultures\' role to play in a sustainable future and wants to take action.\r\n\r\n&nbsp;\r\n\r\nOrganizations, associations, institutions, museums, theaters, opera houses, artists, galleries, conservators, freelance professionals and private practitioners, educators, professors, students, researchers, vendors, suppliers, packing and shipping companies, and anyone else who considers themselves a cultural professional is welcome.\r\n\r\n&nbsp;\r\n\r\nSustainability requires a global effort, and everyone is part of the solution!'),
(24223, 979, '_futures_section_text', 'field_629dece44ddd3'),
