(24541, 980, '_works_section_works_data_list_1_image', 'field_629ee24046b79'),
(24542, 980, 'works_section_works_data_list_1_text', '<h6>The program was co-developed with international sustainability and culture experts and draws from real solutions and with inspiration and collaboration from other sectors.</h6>'),
(24543, 980, '_works_section_works_data_list_1_text', 'field_629ee27346b7a'),
(24544, 980, 'works_section_works_data_list_2_image', '91'),
